import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Credits = (props) => {
  const [credits, setCredits] = useState([]);
  const [description, setDescription] = useState('');
  const [amount, setAmount] = useState('');

  // Fetch credits from the API
  useEffect(() => {
    const fetchCredits = async () => {
      const response = await axios.get('https://Achapko200.github.io/api/credits.json');
      setCredits(response.data);
    };

    fetchCredits();
  }, []);

  // Handle adding a new credit
  const handleAddCredit = (e) => {
    e.preventDefault();
    if (description && amount) {
      const newCredit = {
        description,
        amount: parseFloat(amount).toFixed(2),
        date: new Date().toISOString().split('T')[0], // format yyyy-mm-dd
      };
      setCredits([...credits, newCredit]);
      // Optionally, update the account balance here or in a parent component
      setDescription('');
      setAmount('');
    }
  };

  return (
    <div>
      <h1>Credits</h1>
      <h2>Account Balance: {props.accountBalance.toFixed(2)}</h2>
      <form onSubmit={handleAddCredit}>
        <input
          type="text"
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          required
        />
        <button type="submit">Add Credit</button>
      </form>
      <h3>Credits List</h3>
      <ul>
        {credits.map((credit, index) => (
          <li key={index}>
            {credit.description} - ${credit.amount} on {credit.date}
          </li>
        ))}
      </ul>
      <Link to="/">Return to Home</Link>
    </div>
  );
};

export default Credits;
