/*==================================================
src/components/Debits.js

The Debits component contains information for the Debits page view.
Note: You need to work on this file for the Assignment.
==================================================*/
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Debits = (props) => {
  const [debits, setDebits] = useState([]);
  const [description, setDescription] = useState('');
  const [amount, setAmount] = useState('');

  // Fetch debits from the API
  useEffect(() => {
    const fetchDebits = async () => {
      const response = await axios.get('https://Achapko200.github.io/api/debits.json');
      setDebits(response.data);
    };

    fetchDebits();
  }, []);

  // Handle adding a new debit
  const handleAddDebit = (e) => {
    e.preventDefault();
    if (description && amount) {
      const newDebit = {
        description,
        amount: parseFloat(amount).toFixed(2),
        date: new Date().toISOString().split('T')[0], // format yyyy-mm-dd
        id: debits.length + 1, // Create a simple id
      };
      setDebits([...debits, newDebit]);
      // Optionally, update the account balance here or in a parent component
      setDescription('');
      setAmount('');
    }
  };

  // Create the list of Debit items
  const debitsView = () => {
    return debits.map((debit) => {  // Extract "id", "amount", "description", and "date" properties of each debit JSON array element
      const date = debit.date.slice(0, 10);
      return <li key={debit.id}>{debit.amount} {debit.description} on {date}</li>
    });
  }

  // Render the list of Debit items and a form to input new Debit item
  return (
    <div>
      <h1>Debits</h1>
      <h2>Account Balance: {props.accountBalance.toFixed(2)}</h2>
      <ul>
        {debitsView()}
      </ul>
      <form onSubmit={handleAddDebit}>
        <input
          type="text"
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          required
        />
        <button type="submit">Add Debit</button>
      </form>
      <br/>
      <Link to="/">Return to Home</Link>
    </div>
  );
}

export default Debits;
